(function(){
    var $=jQuery;
    $.fn.popbox = function(options){
        var settings = $.extend({
            selector      : this.selector,
            open          : '.open',
            box           : '.box',
            arrow         : '.arrow',
            arrow_border  : '.arrow-border',
            close         : '.close'
        }, options);

        var methods = {
            open: function(event){
                event.preventDefault();
                $(settings['box']).removeClass("current");
                var pop = $(this);
                var box = $(this).parent().find(settings['box']);
                box.addClass('current');
                box.find(settings['arrow']).css({'left': box.width()/2 - 10});
                box.find(settings['arrow_border']).css({'left': box.width()/2 - 10});

                    methods.close();
                    box.css({'display': 'block', 'top': 8, 'left': (box.width()/2 )});
            },

            close: function(){
                $(settings['box']).each(function(){
                    if(!$(this).hasClass('current'))
                    $(this).fadeOut("fast");
                });
            }
        };

        $(document).bind('keyup', function(event){
            if(event.keyCode == 27){
                $(settings['box']).removeClass('current');
                methods.close();
            }
        });

        $(document).bind('click', function(event){
            if(!$(event.target).closest(settings['selector']).length && !$(event.target).closest('.ui-datepicker').length && !$(event.target).closest('.ui-datepicker-header').length){
                $(settings['box']).removeClass('current');
                methods.close();
            }
        });

        return this.each(function(){
            $(this).css({'width': $(settings['box']).width()}); // Width needs to be set otherwise popbox will not move when window resized.
            $(settings['open'], this).bind('click', methods.open);
            $(settings['open'], this).parent().find(settings['close']).bind('click', function(event){
                event.preventDefault();
                $(settings['box']).removeClass('current');
                methods.close();
            });
        });
    }

}).call(this);
